import { FaLaughBeam } from 'react-icons/fa';
import { FaSmile } from 'react-icons/fa';
import { FaGrinBeamSweat } from 'react-icons/fa';

import { IconContext } from "react-icons";

import { useState } from "react";

//IconContext.Providerのカラーが効かない！！11月7日

const Game = () => {

    //let name = "mario";
    const [name, setName] = useState("mario");
    const [count, setCount] = useState(0);
    const [text, setText] = useState('')
    const [point, getPointIfCorrect] = useState('');

    const changeName = () => {
        if (name === "mario") {
            setName("luigi");
        } else {
            setName("mario")
        }
    }

    const countUp = () => {
        setCount(count + 1);
    }

    const handleSubmit = (e) => {
        e.preventDefault()
        const value = e.target.answer.value;
        console.log(value);
    }

    /*
    const getPointIfCorrect = () => {
        if()
    }*/

    return (
        <div className= "Game" style={{ fontSize: "30px" }}>
            <div className="question"> 
                <p >Vad heter bollen som används i fotboll?</p>

            </div>
            <div className="figure" style={{
                fontSize: "30px",
                marginBottom: "10px"
            }}>Figure Area!
                <IconContext.Provider value={{ color: "#ff6b6b", size: "50px", backgroundColor: "red" }}>
                    <div>
                        <FaLaughBeam />
                        <FaSmile />
                        <FaGrinBeamSweat />
                    </div>
                </IconContext.Provider>
                <p>{name}</p>
                <p>{count}</p>
                <p>{text}</p>
                <p>{point}</p>

            </div>
            <div className="text">
                <form>
                    <label>
                        Text Area:
                        <input type="text" />
                        <input type="text" name="answer"  />
                    </label>
                    <input type="submit" value="Submit" />
                </form>
                <button onClick={changeName}>Change Name</button>
                <button onClick={countUp}>Count Up</button>
                <button onClick={handleSubmit}>Submit</button>


            </div>
        </div>
    );
}

export default Game;
