import { FaLaughBeam } from 'react-icons/fa';
import { FaSmile } from 'react-icons/fa';
import { FaGrinBeamSweat } from 'react-icons/fa';

import { IconContext } from "react-icons";

import { useState } from "react";

//IconContext.Providerのカラーが効かない！！11月7日

const GameArea = () => {

    //let name = "mario";
    const [name, setName] = useState("mario");

    const [count, setCount] = useState(0);

    const changeName = () => {
        setName("luigi");
    }

    const countUp = () => {
        setCount(count + 1);
    }

    return (
        <div className="gamearea" style={{ fontSize: "30px"}}>GameArea
            <div className="figure" style={{
                fontSize: "30px",
                marginBottom: "10px"
            }}>Figure Area!
                <IconContext.Provider value={{ color: "#ff6b6b", size: "50px", backgroundColor: "red" }}>
                    <div>
                        <FaLaughBeam />
                        <FaSmile />
                        <FaGrinBeamSweat />
                    </div>
                </IconContext.Provider>
                <p>{name}</p>
            </div>
            <div className="text">
                <form>
                    <label>
                        Text Area:
                        <input type="text" name="name" />
                    </label>
                    <input type="submit" value="Submit" />
                </form>
                <button onClick={changeName}>Change Name</button>
                <button onClick={(e) => countUp("mario", e)}>Count Up</button>

            </div>
        </div>
    );
}

export default GameArea;
