import { FaLaughBeam } from 'react-icons/fa';
import { FaSmile } from 'react-icons/fa';
import { FaGrinBeamSweat } from 'react-icons/fa';

import { IconContext } from "react-icons";

import { useState } from "react";

//IconContext.Providerのカラーが効かない！！11月7日

const Game = () => {

    const [answer, setAnswer] = useState('Your answer here');

    const checkAnswer = (e) => {

        if(e.target.innerText === 'Apa'){
            console.log("Rätt!")
        } else {
            console.log("Fel!")
        }
    }

    return (
        <div className= "Game" style={{ fontSize: "30px" }}>
            <div className="question"> 
                <p >Vad heter bollen som används i fotboll?</p>

            </div>
            <div className="figure" style={{
                fontSize: "30px",
                marginBottom: "10px"
            }}>Figure Area!
                <IconContext.Provider value={{ color: "#ff6b6b", size: "50px", backgroundColor: "red" }}>
                    <div>
                        <FaLaughBeam />
                        <FaSmile />
                        <FaGrinBeamSweat />
                    </div>
                </IconContext.Provider>


            </div>
            <div className="text">
                <form>
                    <label>
                        Text Area:
                        <input 
                            type="text" 
                            value={answer}
                            onChange={(e) => setAnswer(e.target.value)} />
                    </label>
                    <input type="submit" value="Submit" />
                    <p>{answer}</p>
                </form>
               <button value="Apa" onClick={(e)=> checkAnswer(e)}>Apa</button>
               <button onClick={(e)=> checkAnswer(e)}>Bpa</button>
               <button onClick={(e)=> checkAnswer(e)}>Cpa</button>
               <button onClick={(e)=> checkAnswer(e)}>Dpa</button>

            </div>
        </div>
    );
}

export default Game;
